  status, create_time, update_time
  )
values (#{roleId,jdbcType=INTEGER}, #{roleCd,jdbcType=VARCHAR}, #{roleName,jdbcType=VARCHAR}, 
  #{status,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )
